        form.category = category.parent.name;
        selected.category_id = category.parent.id;
      }
      if (category.informations.length > 0) {
        category.informations.forEach(element => {
          selected.az_inf.push({
            id: element.id,
            title: element.translations[0].title,
            base: true
          });
          selected.en_inf.push({
            id: element.id,
            title: element.translations[1].title,
            base: true
          });
          selected.ru_inf.push({
            id: element.id,
            title: element.translations[2].title,
            base: true
          });
        });
      }
      selected.id = category.id;
      selected.az_name = category.translations[0].name;
      selected.en_name = category.translations[1].name;
      selected.ru_name = category.translations[2].name;
      updatingUrl.value = category.image;
      window.scroll(0, 0);
    };
    const searchCategories = () => {
      page.page = 1;
      loadCategories();
    };
    const order = () => {
      page.order = true;
      loadCategories();
      page.orderBy = !page.orderBy;
    };
    const loadCategories = async () => {
      await axios.post(url + 'loadcategories', page, {
        headers: {
          Authorization: 'Bearer ' + localStorage.getItem('TOKEN')
        }
      }).then(response => {
        categories.value = response.data.categories.data;
        pagesCount.value = response.data.last;
        categoryCount.value = response.data.count;
      });
    };
    const changePage = n => {
      page.page = n;
      loadCategories();
    };
    const previousPage = () => {
      if (page.page != 1) {
        page.page--;
        loadCategories();
      }
    };
    const nextPage = () => {
      if (page.page != pagesCount) {
        page.page++;
        loadCategories();
      }
    };
    const __returned__ = {
      categoryerror,
      categories,
      foundCategory,
      previewUrl,
